INSERT INTO `news` (`id`, `title`, `text_news`, `url`, `created_at`, `updated_at`) VALUES
(1, 'Чешские спецслужбы пожаловались на "крайне высокое" число российских шпионов', '<p>ФСБ РФ проводит масштабную операцию на территории России по выявлению источников финансирования и вербовки бойцов для «Исламского государства» в Сирии.</p>\n							<p>В рамках операции, сообщили источники в правоохранительных органах, с середины октября сотрудники спецслужбы задержали несколько десятков подозреваемых, в том числе и на территории Москвы». «Аналогичные операции проведены и в ряде других регионов России, мероприятия еще продолжаются», — заявили представители правоохранительных органов.</p>\n							<p>В середине октября 2014 года «Аргументы недели» сообщали, что российские спецслужбы и правоохранительные органы получили задание усилить агентурную разработку ячеек «Исламского государства» в РФ. Кроме того, им поручили активизировать действия по отслеживанию и задержанию боевиков, возвращающихся из Сирии, где они воевали на стороне исламистов.</p>', '', '2015-06-04 08:34:51', '0000-00-00 00:00:00'),
(2, 'Депутатов Госдумы обязали сдать дипломатические паспорта', '	<p>Председатель Госдумы Сергей Нарышкин поручил руководителям думских фракций собрать с депутатов дипломатические паспорта. Документы решили забрать из-за «ухудшения отношений» с Евросоюзом и санкций со стороны западных стран.</p>\n							<p>По правилам, депутаты должны сдавать диппаспорта после каждой служебной поездки. В действительности они хранят их у себя и зачастую используют для личных поездок без виз в страны <a href='''' target=''_blank''>Шенгенского соглашения.</a></p>\n							<p>Депутаты должны сдать дипломатические паспорта до 31 октября.</p>', '', '2015-07-04 08:54:51', '0000-00-00 00:00:00'),
